import React from "react";
import { Link } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import "./homepage.css";

export const HomePage = () => {
  const wordVariants = {
    hidden: {
      opacity: 0,
      x: -50,
    },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 2,
      },
    },
  };

  return (
    <AnimatePresence mode='wait'>
      <motion.div
        className="container"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        key="homepage"
      >
        <div className="geometric-design">
          {/* Geometric design here */}
        </div>

        <div className="home-content">
          <div className="main-content" style={{ textAlign: 'left' }}>
            <div className="content-wrapper">
              <div style={{ fontFamily: "monospace" }}>
                <motion.span className="line" initial="hidden" animate="visible" variants={wordVariants} style={{ fontSize: "60px", color: "#7AA095" }}>Welcome!</motion.span>
                <br />
                <motion.span className="line" initial="hidden" animate="visible" variants={wordVariants} style={{ fontSize: "100px", color: "#618B4A" }}>I'm JSM THE BARBER</motion.span>
                <br />
                
                <motion.span className="line" initial="hidden" animate="visible" variants={wordVariants} style={{ fontSize: "30px", color: "#AFBC88" }}>A professional barber and stylist</motion.span>
                <br />
                <motion.span className="line" initial="hidden" animate="visible" variants={wordVariants} style={{ fontSize: "30px", color: "#40531B" }}>Login to book an appointment today!</motion.span>
              </div>
            </div>
          </div>
        </div>
      </motion.div>
    </AnimatePresence>
  );
};
