import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion"; // Import motion from framer-motion
import "./Navbar.css";

const Navbar = ({ isLoggedIn, logout }) => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    console.log("Checking authentication state...");
    const authState = localStorage.getItem("isLoggedIn");
    console.log("Stored authentication state:", authState);
    if (authState === "true") {
      setIsAuthenticated(true);
    } else {
      setIsAuthenticated(false);
    }
  }, []);

  useEffect(() => {
    console.log("isAuthenticated state changed:", isAuthenticated);
  }, [isAuthenticated]);

  const handleLogout = () => {
    localStorage.removeItem("isLoggedIn");
    setIsAuthenticated(false);
    logout();
  };

  console.log("Rendering Navbar with isAuthenticated:", isAuthenticated);

  return (
    <ul className="navbar">
      <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
        <Link to="/">Home</Link>
      </motion.li>
      {!isAuthenticated && (
        <>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="/signup">Sign Up</Link>
          </motion.li>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="/login">Login</Link>
          </motion.li>
        </>
      )}
      {isAuthenticated && (
        <>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="/ScheduleAppt">Schedule</Link>
          </motion.li>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <button onClick={handleLogout}>Logout</button>
          </motion.li>
        </>
      )}
    </ul>
  );
};

export default Navbar;
