import React, { useState } from 'react';
import './LoginSignUp.css';

import user_icon from '../Assets/person.png';
import email_icon from '../Assets/email.png';
import password_icon from '../Assets/password.png';

export const LoginSignUp = () => {
  const [email, setEmail] = useState('')
  const [fullName, setFullName] = useState('')
  const [password, setPassword] = useState('')
  const [action, setAction] = useState("Sign Up");

  return (
    <div>
      <div className="navbar">
      <a href="/" className="left">JSM's Chop Shop</a>
        <div className="right">
          <button onClick={() => setAction("Sign Up")}>Sign Up</button>
          <button onClick={() => setAction("Login")}>Login</button>
        </div>
      </div>
      <div className="LS-container">
        <div className="header">
          <div className="text">{action}</div>
          <div className="underline"></div>
        </div>
        <div className="inputs">
          {action === "Login" ? null : (
            <div className="input">
              <img src={user_icon} alt="" />
              <input type="text" placeholder='Full Name'onChange={e => setEmail(e.target.value)}/>
            </div>
          )}
          <div className="input">
            <img src={email_icon} alt="" />
            <input type="email" placeholder='Email Address' onChange={e => setEmail(e.target.value)}/>
          </div>
          <div className="input">
            <img src={password_icon} alt="" />
            <input type="password" placeholder='Password' onChange={e => setPassword(e.target.value)}/>
          </div>
          {action === "Sign Up" ? null : (
          <div className="forgot-password">Forgot Password? <span>Click Here!</span></div>
        )}
          <div className="submit-btn">Submit</div>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
