import React, { useState } from "react";
import Axios from "axios";

import user_icon from "../Assets/person.png";
import email_icon from "../Assets/email.png";
import password_icon from "../Assets/password.png";

export const SignUp = () => {
  const [email, setEmail] = useState("");
  const [fullName, setFullName] = useState("");
  const [password, setPassword] = useState("");
  const [signUpStatus, setSignUpStatus] = useState("");
  const [action, setAction] = useState("Sign Up");

  const signUp = (e) => {
    e.preventDefault();
    Axios.post("http://localhost:3001/signUp", {
      fullName: fullName,
      email: email,
      password: password,
    }).then((response) => {
      if (response.data.message) {
        setSignUpStatus(response.data.message);
      } else {
        setSignUpStatus("ACCOUNT CREATED SUCCESSFULLY");
      }
    })
  }

  return (
    <div>
      <div className="navbar">
        <a href="/" className="left">
          JSM's Chop Shop
        </a>
        <div className="right">
          <button onClick={() => setAction("Sign Up")}>Sign Up</button>
          <button onClick={() => setAction("Login")}>Login</button>
        </div>
      </div>
      <div className="LS-container">
        <div className="header">
          <div className="text">{action}</div>
          <div className="underline"></div>
        </div>
        <form  className="inputs">
          {action === "Login" ? null : (
            <div className="input">
              <img src={user_icon} alt="" />
              <input
                type="text"
                placeholder="Full Name"
                onChange={(e) => {
                  setFullName(e.target.value);
                }}
              />
            </div>
          )}
          <div className="input">
            <img src={email_icon} alt="" />
            <input
              type="email"
              placeholder="Email Address"
              onChange={(e) => {
                setEmail(e.target.value);
              }}
            />
          </div>
          <div className="input">
            <img src={password_icon} alt="" />
            <input
              type="password"
              placeholder="Password"
              onChange={(e) => {
                setPassword(e.target.value);
              }}
            />
          </div>
          {action === "Sign Up" ? null : (
            <div className="forgot-password">
              Forgot Password? <span>Click Here!</span>
            </div>
          )}
          <h1 style={{fontSize:'15px', textAlign: 'center', marginTop: '20px',}}>{signUpStatus}</h1>
          <div className="submit-btn" onClick={signUp}>
            Submit
          </div>
        </form>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
