import React, { useState } from "react";
import "./Login.css";
import { Link } from "react-router-dom";
import Axios from "axios";
import Navbar from "../Navbar/Navbar";
import transition from "../transition";

export const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginStatus, setLoginStatus] = useState("");

  const login = (e) => {
    e.preventDefault();
    if (!email || !password) {
      // Check if email or password is empty
      setLoginStatus("Please fill in all fields.");
      return;
    }
    Axios.post("http://localhost:3001/login", {
      email: email,
      password: password,
    }).then((response) => {
      if (response.data.message) {
        setLoginStatus(response.data.message);
      } else {
        setLoginStatus("LOGIN SUCCESSFUL");
      }
    });
  };

  return (
    <div>
      <Navbar />
      <div class="login-container">
        <form class="login-form">
          <p class="heading">Login</p>
          <p class="paragraph">Login to your account</p>
          <div class="input-group">
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              required
              placeholder="Email"
              id="email"
              type="email" // Use type="email" for email validation
            />
          </div>
          <div class="input-group">
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              required
              placeholder="Password"
              name="password"
              id="password"
              type="password"
            />
          </div>
          <h1 style={{ color: "red", fontSize: "15px", textAlign: "center", marginTop: "20px" }}>
            {loginStatus}
          </h1>
          <button type="submit" onClick={login}>
            Login
          </button>
          <div class="bottom-text">
            <p>
              Don't have an account? <Link to="#">Sign Up</Link>
            </p>
            <p>
              <Link to="#">Forgot password?</Link>
            </p>
          </div>
        </form>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};

export default transition(HomePage);
