import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { motion } from "framer-motion"; // Import motion from framer-motion
import "./Navbar.css";

const Navbar = ({ logout }) => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    // Check if authentication state exists in local storage
    const authState = localStorage.getItem("isLoggedIn");
    if (authState === "true") {
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogout = () => {
    // Clear authentication state from local storage
    localStorage.removeItem("isLoggedIn");
    setIsLoggedIn(false);
    logout();
  };

  return (
    <ul className="navbar">
      <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
        <Link to="/">Home</Link>
      </motion.li>
      {!isLoggedIn && (
        <>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="/signup">Sign Up</Link>
          </motion.li>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="/login">Login</Link>
          </motion.li>
        </>
      )}
      {isLoggedIn && (
        <>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="/ScheduleAppt">Schedule</Link>
          </motion.li>
          <motion.li className="navbar-item" whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
            <Link to="#" onClick={handleLogout}>Logout</Link>
          </motion.li>
        </>
      )}
    </ul>
  );
};

export default Navbar;
