import React, { useState } from "react";
import { Link, useLocation } from "react-router-dom"; // Import Link
import "./homepage.css";
import { motion } from "framer-motion";
import Barbershop_pic from "../Assets/Barbershop.jpg";


export const HomePage = () => {
  const location = useLocation();

  const getPageBackgroundClass = () => {
    if (location.pathname === "/") {
      return "page1-background";
    } else if (location.pathname === "/page2") {
      return "page2-background";
    }
    // Default background color
    return "";
  };

  return (
    <div className={`home-page-container ${getPageBackgroundClass()}`}>
      <div className="navbar">
        <a href="/" className="left">
          JSM's Chop Shop
        </a>
        <div className="right">
          {/* Use Link to navigate to "/LoginSignUp" */}
          <Link to="/SignUp">
            <button>Sign Up</button>
          </Link>
          <Link to="/Login">
            <button>Login</button>
          </Link>
        </div>
      </div>
      <div className="home-content">
        <div className="main-content">
          <motion.h2
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 2 }}
            style={{ fontFamily:"serif",fontSize: "200px", color: "#E8D1C5" }}
          >
            JSMTheBarber
          </motion.h2>
          <Link to="/ScheduleAppt">
            <button>
              <span class="transition"></span>
              <span class="gradient"></span>
              <span class="label">Book Now</span>
            </button>
          </Link>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
