import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes, useNavigate } from "react-router-dom";
import { AnimatePresence, motion } from "framer-motion"; // Import AnimatePresence and motion from framer-motion
import { HomePage } from "./Homepage/homepage";
import { ScheduleAppt } from "./ScheduleAppt/ScheduleAppt";
import { Login } from "./Login/Login";
import { SignUp } from "./SignUp/SignUp";
import Navbar from "./Navbar/Navbar";

function RouterFunction() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const navigate = useNavigate(); // Use useNavigate hook to navigate between routes

  const handleLogin = () => {
    setIsLoggedIn(true);
    navigate('/'); // Redirect to home after successful login
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    localStorage.removeItem("token"); // Clear the token from local storage
    navigate('/'); // Redirect to home after logout
  };

  return (
    <div>
      <Router>
        <Navbar isLoggedIn={isLoggedIn} logout={handleLogout} />
        <AnimatePresence mode='wait'> {/* Wrap Routes with AnimatePresence */}
          <Routes>
            <Route path="/" element={<HomePage />} />
            <Route path="/Login" element={<Login onLogin={handleLogin} />} /> {/* Pass handleLogin as a prop */}
            <Route path="/SignUp" element={<SignUp />} />
            {isLoggedIn && <Route path="/ScheduleAppt" element={<ScheduleAppt />} />}
          </Routes>
        </AnimatePresence>
      </Router>
    </div>
  );
}

export default RouterFunction;
