import React, { useState } from "react";
import emailjs from "emailjs-com";
import "./ScheduleAppt.css";
import Navbar from "../Navbar/Navbar";

export const ScheduleAppt = () => {
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [dateError, setDateError] = useState("");
  const [timeError, setTimeError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [fullNameError, setFullNameError] = useState("");

  const currentDate = new Date();
  const oneMonthLater = new Date(
    currentDate.getFullYear(),
    currentDate.getMonth() + 1,
    currentDate.getDate()
  );

  const isTimeValid = (time) => {
    const [hours, minutes] = time.split(":");
    const minutesInt = parseInt(minutes);
    return minutesInt === 0 || minutesInt === 15 || minutesInt === 30 || minutesInt === 45;
  };

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
    if (fullNameError && event.target.value.trim()) {
      setFullNameError("");
    }
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
    if (dateError) {
      setDateError("");
    }
  };

  const handleTimeChange = (event) => {
    const enteredTime = event.target.value;

    if (isTimeValid(enteredTime)) {
      setSelectedTime(enteredTime);
      setTimeError("");
    } else {
      setTimeError("Please choose a time in 15-minute increments between 10AM-6PM");
    }
  };

  const sendEmail = () => {
    // Format the date
    const dateParts = selectedDate.split("-");
    const formattedDate = `${getMonthName(parseInt(dateParts[1]) - 1)} ${
      dateParts[2]
    }, ${dateParts[0]}`;

    // Format the time
    const timeParts = selectedTime.split(":");
    const hour = parseInt(timeParts[0]) % 12 || 12;
    const formattedTime = `${hour}:${timeParts[1]} ${
      parseInt(timeParts[0]) < 12 ? "am" : "pm"
    }`;

    const templateParams = {
      clientName: fullName,
      appointmentDate: formattedDate,
      appointmentTime: formattedTime,
    };

    emailjs
      .send(
        "service_kcjl9sl",
        "template_kl6j27m",
        templateParams,
        "8G1sbA6KokAhfReWa"
      )
      .then(
        (result) => {
          console.log(result.text);
          setFormSubmitted(true);
          setSuccessMessage("Appointment successfully scheduled!");

          // Reset form after submission
          setFullName("");
          setSelectedDate("");
          setSelectedTime("");
        },
        (error) => {
          console.error(error.text);
          // Handle error, e.g., show error message to the user
        }
      );
  };

  const handleScheduleAppointment = () => {
    let hasError = false;

    if (!fullName.trim()) {
      setFullNameError("Please enter your full name.");
      hasError = true;
    } else {
      setFullNameError("");
    }

    if (!selectedDate) {
      setDateError("Please select a date.");
      hasError = true;
    } else {
      setDateError("");
    }

    if (!selectedTime) {
      setTimeError("Please select a time.");
      hasError = true;
    } else {
      setTimeError("");
    }

    if (!hasError) {
      if (!formSubmitted) {
        sendEmail();
      }
    }
  };

  // Helper function to get month name
  const getMonthName = (monthIndex) => {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    return months[monthIndex];
  };

  return (
    <div>
      <Navbar />
      <div className="S-container">
        <div className="appointment-container">
          <form id="appointment-form" className="appointment-form">
            <p className="sign-up-heading">Schedule Appointment</p>
            <p className="paragraph">Schedule an appointment today</p>
            <div className="input-group">
              <input
                required
                type="text"
                placeholder="Full Name"
                value={fullName}
                onChange={handleFullNameChange}
              />
              {fullNameError && (
                <div className="error-message">{fullNameError}</div>
              )}
            </div>
            <div className="input-group">
              <input
                required
                type="date"
                placeholder="Date"
                value={selectedDate}
                onChange={handleDateChange}
                min={currentDate.toISOString().split("T")[0]}
                max={oneMonthLater.toISOString().split("T")[0]}
              />
              {dateError && <div className="error-message">{dateError}</div>}
            </div>
            <div className="input-group">
              <input
                required
                type="time"
                placeholder="Time"
                value={selectedTime}
                onChange={handleTimeChange}
                min="10:00"
                max="18:00"
              />
              {timeError && <div className="error-message">{timeError}</div>}
            </div>
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
            {successMessage && (
              <div className="success-message">{successMessage}</div>
            )}
          </form>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
