import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link
import "./SignUp.css";
import Axios from "axios";

export const SignUp = () => {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [signUpStatus, setSignUpStatus] = useState({ message: "", color: "" });

  const signUp = (e) => {
    e.preventDefault();
    Axios.post("http://localhost:3001/signUp", {
      fullName: fullName,
      email: email,
      password: password,
    }).then((response) => {
      if (response.data.message) {
        setSignUpStatus(response.data.message);
      } else {
        setSignUpStatus("ACCOUNT CREATED SUCCESSFULLY!");
      }
    })
  }

  return (
    <div>
      <div className="navbar">
        <a href="/" className="left">
          JSM's Chop Shop
        </a>
        <div className="right">
          <Link to="/SignUp">
            <button>Sign Up</button>
          </Link>
          <Link to="/Login">
            <button>Login</button>
          </Link>
        </div>
      </div>
      <div class="signUp-container">
        <form class="signUp-form">
          <p class="sign-up-heading">Sign Up</p>
          <p class="paragraph">Sign up for an account</p>
          <div class="input-group">
            <input
              onChange={(e) => {
                setFullName(e.target.value);
              }}
              required=""
              placeholder="Full Name"
              id="fullName"
              type="text"
            />
          </div>
          <div class="input-group">
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              required=""
              placeholder="Email"
              id="email"
              type="text"
            />
          </div>
          <div class="input-group">
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              required=""
              placeholder="Password"
              name="password"
              id="password"
              type="password"
            />
          </div>
          <h1 style={{color:'green', fontSize:'15px', textAlign:'center', marginTop:'20px'}}>{signUpStatus}</h1>
          <button type="submit" onClick={signUp}>
            Sign Up
          </button>
          <div class="bottom-text">
            <p>
              Already have an account? <a href="#">Login</a>
            </p>
            <p>
              <a href="#">Forgot password?</a>
            </p>
          </div>
        </form>
      </div>

      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
