import React, { useState } from 'react';
import './homepage.css';


export const ScheduleAppt = () => {
  const [action, setAction] = useState("Sign Up");

  // State variables to store form data
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");

  // Function to handle form submission
  const handleSchedule = () => {
    // Perform scheduling action here
    console.log("Scheduled:", { fullName, selectedDate, selectedTime });
  };

  // JSX content for schedule appointment form
  const scheduleForm = (
    <div className="main-content">
      <h2>Schedule Appointment</h2>
      <form onSubmit={handleSchedule}>
        <div className="form-group">
          <label>Full Name:</label>
          <input
            type="text"
            value={fullName}
            onChange={(e) => setFullName(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Date:</label>
          <input
            type="date"
            value={selectedDate}
            onChange={(e) => setSelectedDate(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label>Time:</label>
          <input
            type="time"
            value={selectedTime}
            onChange={(e) => setSelectedTime(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="schedule-btn">Schedule</button>
      </form>
    </div>
  );

  return (
    <div>
      <div className="navbar">
        <div className="left">JSM's Chop Shop</div>
        <div className="right">
          <button onClick={() => setAction("Sign Up")}>Sign Up</button>
          <button onClick={() => setAction("Login")}>Login</button>
        </div>
      </div>
      {scheduleForm}
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
