import React, { useState } from "react";
import emailjs from "emailjs-com";
import "./ScheduleAppt.css";
import axios from 'axios';

export const ScheduleAppt = () => {
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [dateError, setDateError] = useState("");
  const [timeError, setTimeError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [fullNameError, setFullNameError] = useState("");

  const currentDate = new Date();
  const oneMonthLater = new Date(
    currentDate.getFullYear(),
    currentDate.getMonth() + 1,
    currentDate.getDate()
  );

  const availableTimes = [
    "10:00 AM", "11:00 AM", "12:00 PM", "01:00 PM", "02:00 PM", "03:00 PM", "04:00 PM", "05:00 PM", "06:00 PM"
  ];

  const bookedTimes = []; // Add booked times here

  const handleTimeSelection = (time) => {
    if (!bookedTimes.includes(time)) {
      setSelectedTime(time);
      setTimeError("");
    } else {
      setTimeError("This time slot is already booked. Please select another time.");
    }
  };

  const sendEmail = () => {
    // Format the date
    const dateParts = selectedDate.split("-");
    const formattedDate = `${getMonthName(parseInt(dateParts[1]) - 1)} ${
      dateParts[2]
    }, ${dateParts[0]}`;

    // Format the time
    const formattedTime = selectedTime;

    const templateParams = {
      clientName: fullName,
      appointmentDate: formattedDate,
      appointmentTime: formattedTime,
    };

    emailjs
      .send(
        "service_kcjl9sl",
        "template_kl6j27m",
        templateParams,
        "8G1sbA6KokAhfReWa"
      )
      .then(
        (result) => {
          console.log(result.text);
          setFormSubmitted(true);
          setSuccessMessage("Appointment successfully scheduled!");

          // Reset form after submission
          setFullName("");
          setSelectedDate("");
          setSelectedTime("");
        },
        (error) => {
          console.error(error.text);
          // Handle error, e.g., show error message to the user
        }
      );
  };

  const handleScheduleAppointment = () => {
    let hasError = false;

    if (!fullName.trim()) {
      setFullNameError("Please enter your full name.");
      hasError = true;
    } else {
      setFullNameError("");
    }

    if (!selectedDate) {
      setDateError("Please select a date.");
      hasError = true;
    } else {
      setDateError("");
    }

    if (!selectedTime) {
      setTimeError("Please select a time.");
      hasError = true;
    } else {
      setTimeError("");
    }

    if (!hasError) {
      if (!formSubmitted) {
        // Make an HTTP POST request to the backend
        axios.post('http://localhost:3001/ScheduleAppt', {
          fullName: fullName,
          date: selectedDate,
          time: selectedTime
        })
        .then((response) => {
          console.log(response.data);
          setFormSubmitted(true);
          setSuccessMessage("Appointment successfully scheduled!");

          // Reset form after submission
          setFullName("");
          setSelectedDate("");
          setSelectedTime("");
        })
        .catch((error) => {
          console.error(error);
          // Handle error, e.g., show error message to the user
        });
      }
    }
  };

  // Helper function to get month name
  const getMonthName = (monthIndex) => {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    return months[monthIndex];
  };

  return (
    <div>
      <div className="S-container">
        <div className="appointment-container">
          <form id="appointment-form" className="appointment-form">
            <p className="sign-up-heading">Schedule Appointment</p>
            <p className="paragraph">Schedule an appointment today</p>
            <div className="input-group">
              <input
                required
                type="text"
                placeholder="Full Name"
                value={fullName}
                onChange={(event) => setFullName(event.target.value)}
              />
              {fullNameError && (
                <div className="error-message">{fullNameError}</div>
              )}
            </div>
            <div className="input-group">
              <input
                required
                type="date"
                placeholder="Date"
                value={selectedDate}
                onChange={(event) => setSelectedDate(event.target.value)}
                min={currentDate.toISOString().split("T")[0]}
                max={oneMonthLater.toISOString().split("T")[0]}
              />
              {dateError && <div className="error-message">{dateError}</div>}
            </div>
            <div className="input-group">
              {availableTimes.map((time, index) => (
                <div
                  key={index}
                  onClick={() => handleTimeSelection(time)}
                  className={`time-button ${selectedTime === time || bookedTimes.includes(time) ? "selected" : ""}`}
                  disabled={bookedTimes.includes(time)}
                >
                  {time}
                </div>
              ))}
            </div>
            {timeError && <div className="error-message">{timeError}</div>}
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
            {successMessage && (
              <div className="success-message">{successMessage}</div>
            )}
          </form>
        </div>
      </div>
    </div>
  );
};
