import React from "react";
import { Link } from "react-router-dom";
import { motion, AnimatePresence } from "framer-motion";
import "./homepage.css";

export const HomePage = () => {
  const wordVariants = {
    hidden: {
      opacity: 0,
      x: -50,
    },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 2,
      },
    },
  };

  return (
    <AnimatePresence mode='wait'>
      <motion.div
        className="container"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
        key="homepage"
      >
        <div className="home-content">
          <div className="main-content">
            <div className="content-wrapper">
              <h2>Welcome!</h2>
              <div className="custom-text">
                <motion.div initial="hidden" animate="visible" variants={wordVariants} style={{ display: "inline-block" }}>
                  <motion.span variants={wordVariants}>I'm JSM THE BARBER</motion.span>
                </motion.div>
              </div>
              <p>A professional barber and stylist</p>
              <p>Login to book an appointment today!</p>
            </div>
          </div>
        </div>
      </motion.div>
    </AnimatePresence>
  );
};

// Example of how to use motion with react-router-dom Link
export const Navigation = () => {
  return (
    <div>
      <Link to="/home">
        <motion.button whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
          HOME
        </motion.button>
      </Link>
      <Link to="/signup">
        <motion.button whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
          SIGNUP
        </motion.button>
      </Link>
      <Link to="/login">
        <motion.button whileHover={{ scale: 1.1 }} whileTap={{ scale: 0.9 }}>
          LOGIN
        </motion.button>
      </Link>
    </div>
  );
};
