import React, { useState } from "react";
import "./Login.css";
import { Link, useNavigate } from "react-router-dom"; // Import useNavigate
import Axios from "axios";

export const Login = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginStatus, setLoginStatus] = useState("");
  const navigate = useNavigate(); // Initialize useNavigate

  const login = (e) => {
    e.preventDefault();
    if (!email || !password) {
      // Check if email or password is empty
      setLoginStatus("Please fill in all fields.");
      return;
    }
    Axios.post("http://localhost:3001/login", {
      email: email,
      password: password,
    }).then((response) => {
      if (response.data.token) {
        localStorage.setItem("token", response.data.token);
        setLoginStatus("LOGIN SUCCESSFUL");
        navigate("/"); // Redirect to homepage
      } else {
        setLoginStatus(response.data.message || "Login Failed");
      }
    })
    .catch((error) => {
      console.error("Login error: ", error);
      setLoginStatus("Incorrect email or password");
    });
  };

  return (
    <div>
      <div class="login-container">
        <form class="login-form">
          <p class="heading">Login</p>
          <p class="paragraph">Login to your account</p>
          <div class="input-group">
            <input
              onChange={(e) => {
                setEmail(e.target.value);
              }}
              required
              placeholder="Email"
              id="email"
              type="email" // Use type="email" for email validation
            />
          </div>
          <div class="input-group">
            <input
              onChange={(e) => {
                setPassword(e.target.value);
              }}
              required
              placeholder="Password"
              name="password"
              id="password"
              type="password"
            />
          </div>
          <h1
            style={{
              color: "red",
              fontSize: "15px",
              textAlign: "center",
              marginTop: "20px",
            }}
          >
            {loginStatus}
          </h1>
          <button type="submit" onClick={login}>
            Login
          </button>
          <div class="bottom-text">
            <p>
              Don't have an account? <a href="./SignUp">Sign Up</a>
            </p>
            <p>
              <Link to="#">Forgot password?</Link>
            </p>
          </div>
        </form>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
