import React, { useState } from "react";
import "./ScheduleAppt.css";
import { Link } from "react-router-dom";
import Navbar from "../Navbar/Navbar";

export const ScheduleAppt = () => {
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [dateError, setDateError] = useState("");
  const [timeError, setTimeError] = useState("");

  const currentDate = new Date();
  const oneMonthLater = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, currentDate.getDate());

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  const handleDateChange = (event) => {
    const selectedDate = new Date(event.target.value);

    if (selectedDate < currentDate || selectedDate >= oneMonthLater.setDate(oneMonthLater.getDate() + 1)) {
      setDateError("Please choose a date from the current day to 1 month from the current day.");
    } else {
      setDateError("");
      setSelectedDate(event.target.value);
    }
  };

  const handleTimeChange = (event) => {
    const selectedTime = new Date(`2000-01-01T${event.target.value}`);
    const start = new Date(`2000-01-01T10:00`);
    const end = new Date(`2000-01-01T18:00`);

    if (selectedTime < start || selectedTime > end) {
      setTimeError("Please choose a time between 10am-6pm.");
    } else {
      setTimeError("");
      setSelectedTime(event.target.value);
    }
  };

  const handleScheduleAppointment = () => {
    if (!dateError && !timeError) {
      setFormSubmitted(true);
    }
  };

  return (
    <div>
      <Navbar />
      <div className="S-container">
        <div className="appointment-container">
          <form className="appointment-form">
            <p className="sign-up-heading">Schedule Appointment</p>
            <p className="paragraph">Schedule an appointment today</p>
            <div className="input-group">
              <input
                required
                type="text"
                placeholder="Full Name"
                value={fullName}
                onChange={handleFullNameChange}
              />
            </div>
            <div className="input-group">
              <input
                required
                type="date"
                placeholder="Date"
                value={selectedDate}
                onChange={handleDateChange}
                min={currentDate.toISOString().split("T")[0]}
                max={oneMonthLater.toISOString().split("T")[0]}
              />
              {dateError && <div className="error">{dateError}</div>}
            </div>
            <div className="input-group">
              <input
                required
                type="time"
                placeholder="Time"
                value={selectedTime}
                onChange={handleTimeChange}
                min="10:00"
                max="18:00"
              />
              {timeError && <div className="error">{timeError}</div>}
            </div>
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
          </form>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
