import React, { useState } from 'react';
import './ScheduleAppt.css';

export const ScheduleAppt = () => {
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setSelectedTime(event.target.value);
  };

  const handleScheduleAppointment = () => {
    // Handle scheduling appointment logic here
    console.log("Scheduled appointment:", fullName, selectedDate, selectedTime);
  };

  return (
    <div>
      <div className="navbar">
        <div className="left">JSM's Chop Shop</div>
        <div className="right">
          {/* Buttons for Sign Up and Login can be added here */}
        </div>
      </div>
      <div className="container">
        <h2 className="form-header">Schedule Appointment</h2>
        <div className="appointment-container">
          <form className="appointment-form">
            <input
              type="text"
              placeholder="Full Name"
              value={fullName}
              onChange={handleFullNameChange}
            />
            <input
              type="date"
              placeholder="Date"
              value={selectedDate}
              onChange={handleDateChange}
            />
            <input
              type="time"
              placeholder="Time"
              value={selectedTime}
              onChange={handleTimeChange}
            />
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
          </form>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
