import React, { useState } from "react";
import emailjs from "emailjs-com";
import "./ScheduleAppt.css";
import Navbar from "../Navbar/Navbar";

export const ScheduleAppt = () => {
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [formSubmitted, setFormSubmitted] = useState(false);
  const [dateError, setDateError] = useState("");
  const [timeError, setTimeError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  const currentDate = new Date();
  const oneMonthLater = new Date(
    currentDate.getFullYear(),
    currentDate.getMonth() + 1,
    currentDate.getDate()
  );

  const TimePicker = ({ value, onChange }) => {
    const hours = [...Array(12).keys()].map((hour) => (hour === 0 ? 12 : hour));
    const minutes = ["00", "15", "30", "45"];
    const ampm = ["AM", "PM"];

    return (
      <div className="time-picker-container">
        <select
          value={value.split(" ")[0]}
          onChange={(e) => onChange(`${e.target.value} ${value.split(" ")[1]}`)}
          className="time-picker"
        >
          {hours.map((hour) =>
            minutes.map((minute) => (
              <option key={`${hour}:${minute}`} value={`${hour}:${minute}`}>
                {`${hour}:${minute}`}
              </option>
            ))
          )}
        </select>
        <select
          value={value.split(" ")[1]}
          onChange={(e) => onChange(`${value.split(" ")[0]} ${e.target.value}`)}
          className="time-picker"
        >
          {ampm.map((period) => (
            <option key={period} value={period}>
              {period}
            </option>
          ))}
        </select>
      </div>
    );
  };

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    if (event.target.value) {
      const selectedTimeValue = event.target.value;
      const [hours, minutes, period] = selectedTimeValue.split(/[:, ]/);
      const selectedTime = new Date(`2000-01-01T${hours}:${minutes} ${period}`);
      const start = new Date(`2000-01-01T10:00 AM`);
      const end = new Date(`2000-01-01T06:00 PM`);

      if (selectedTime < start || selectedTime >= end) {
        setTimeError("Please choose a time between 10am-6pm.");
      } else {
        setTimeError("");
        setSelectedTime(selectedTimeValue);
      }
    } else {
      setSelectedTime("");
      setTimeError("");
    }
  };

  const sendEmail = () => {
    // Format the date
    const dateParts = selectedDate.split("-");
    const formattedDate = `${getMonthName(parseInt(dateParts[1]) - 1)} ${
      dateParts[2]
    }, ${dateParts[0]}`;

    // Format the time
    const formattedTime = selectedTime;

    const templateParams = {
      clientName: fullName,
      appointmentDate: formattedDate,
      appointmentTime: formattedTime,
    };

    emailjs
      .send(
        "service_kcjl9sl",
        "template_kl6j27m",
        templateParams,
        "8G1sbA6KokAhfReWa"
      )
      .then(
        (result) => {
          console.log(result.text);
          setFormSubmitted(true);
          setSuccessMessage("Appointment successfully scheduled!");

          // Reset form after submission
          setFullName("");
          setSelectedDate("");
          setSelectedTime("");
        },
        (error) => {
          console.error(error.text);
          // Handle error, e.g., show error message to the user
        }
      );
  };

  const handleScheduleAppointment = () => {
    if (!formSubmitted) {
      if (!selectedDate) {
        setDateError("Please select a date.");
      } else if (!selectedTime) {
        setTimeError("Please select a time.");
      } else {
        sendEmail();
      }
    }
  };

  // Helper function to get month name
  const getMonthName = (monthIndex) => {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December",
    ];
    return months[monthIndex];
  };

  return (
    <div>
      <Navbar />
      <div className="S-container">
        <div className="appointment-container">
          <form id="appointment-form" className="appointment-form">
            <p className="sign-up-heading">Schedule Appointment</p>
            <p className="paragraph">Schedule an appointment today</p>
            <div className="input-group">
              <input
                required
                type="text"
                placeholder="Full Name"
                value={fullName}
                onChange={handleFullNameChange}
              />
            </div>
            <div className="input-group">
              <input
                required
                type="date"
                placeholder="Date"
                value={selectedDate}
                onChange={handleDateChange}
                min={currentDate.toISOString().split("T")[0]}
                max={oneMonthLater.toISOString().split("T")[0]}
              />
              {formSubmitted && !selectedDate && (
                <div className="error-message">{dateError}</div>
              )}
            </div>
            <div className="input-group">
              <TimePicker value={selectedTime} onChange={handleTimeChange} />
              {formSubmitted && !selectedTime && (
                <div className="error-message">{timeError}</div>
              )}
            </div>
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
            {successMessage && (
              <div className="success-message">{successMessage}</div>
            )}
          </form>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
