// RouterFunction.js

import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { HomePage } from "./Homepage/homepage";
import { ScheduleAppt } from "./ScheduleAppt/ScheduleAppt";
import { Login } from "./Login/Login";
import { SignUp } from "./SignUp/SignUp";
import Navbar from "./Navbar/";

function RouterFunction() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = () => {
    setIsLoggedIn(true);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    // Additional logout logic like clearing local storage can be added here
  };

  return (
    <div>
      <Router>
        <Navbar isLoggedIn={isLoggedIn} logout={handleLogout} />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/Login" element={<Login onLogin={handleLogin} />} /> {/* Pass handleLogin as a prop */}
          <Route path="/SignUp" element={<SignUp />} />
          {isLoggedIn && <Route path="/ScheduleAppt" element={<ScheduleAppt />} />}
        </Routes>
      </Router>
    </div>
  );
}

export default RouterFunction;
