import React, { useState } from 'react';
import './ScheduleAppt.css';
import { Link } from 'react-router-dom'; // Import Link
import Navbar from "../Navbar/Navbar";

export const ScheduleAppt = () => {
  const [action, setAction] = useState("Sign Up");
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [formSubmitted, setFormSubmitted] = useState(false);

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setSelectedTime(event.target.value);
  };

  const handleScheduleAppointment = () => {
    setFormSubmitted(true);

    // Check if the form is valid
    const currentDate = new Date();
    const endOfMonth = new Date(currentDate.getFullYear(), currentDate.getMonth() + 1, 0);
    const selectedDateObj = new Date(selectedDate);
    const selectedTimeObj = new Date(`2000-01-01 ${selectedTime}`);

 const currentDateOnly = new Date(currentDate.getFullYear(), currentDate.getMonth(), currentDate.getDate());
  const selectedDateOnly = new Date(selectedDateObj.getFullYear(), selectedDateObj.getMonth(), selectedDateObj.getDate());



    if (
      selectedDateObj < currentDate ||
      selectedDateObj > endOfMonth ||
      selectedTimeObj < new Date("2000-01-01T10:00:00") ||
      selectedTimeObj > new Date("2000-01-01T18:00:00")
    ) {
      // Invalid form, do not proceed
      return;
    }

    // Handle scheduling appointment logic here
    console.log("Scheduled appointment:", fullName, selectedDate, selectedTime);
  };

  return (
    <div>
      <Navbar />
      <div className="S-container">
        <div className="appointment-container">
          <form className="appointment-form">
          <p className="sign-up-heading">Schedule Appointment</p>
          <p className="paragraph">Schedule an appointment today</p>
          <div className="input-group">
            <input
              required
              type="text"
              placeholder="Full Name"
              value={fullName}
              onChange={handleFullNameChange}
            />
            </div>
            <div className="input-group">
            <input
              required
              type="date"
              placeholder="Date"
              value={selectedDate}
              onChange={handleDateChange}
            />
            {formSubmitted && ( // Show error if form submitted and date is invalid
              (new Date(selectedDate) < new Date() ||
                new Date(selectedDate) > new Date(new Date().getFullYear(), new Date().getMonth() + 1, 0)) && (
                <p className="error-message">Please select a date between today and the end of the current month.</p>
              )
            )}
            </div>
            <div className="input-group">
            <input
              required
              type="time"
              placeholder="Time"
              value={selectedTime}
              onChange={handleTimeChange}
            />
            {formSubmitted && ( // Show error if form submitted and time is invalid
              (new Date(`2000-01-01 ${selectedTime}`) < new Date("2000-01-01T10:00:00") ||
                new Date(`2000-01-01 ${selectedTime}`) > new Date("2000-01-01T18:00:00")) && (
                <p className="error-message">Please select a time between 10:00 AM and 6:00 PM.</p>
              )
            )}
            </div>
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
          </form>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
