import React, { useState } from "react";
import { Link, Route, us } from "react-router-dom"; // Import Link
import "./homepage.css";
import { motion, AnimatePresence } from "framer-motion";

const pageVariants = {
  initial: {
    opacity: 0,
    x: "-100vw",
  },
  in: {
    opacity: 1,
    x: 0,
  },
  out: {
    opacity: 0,
    x: "100vw",
  },
};


export const HomePage = () => {

  return (
    <div>
      <div className="navbar">
        <a href="/" className="left">
          JSM's Chop Shop
        </a>
        <div className="right">
          {/* Use Link to navigate to "/LoginSignUp" */}
          <Link to="/SignUp">
            <button>Sign Up</button>
          </Link>
          <Link to="/Login">
            <button>Login</button>
          </Link>
        </div>
      </div>
      <div className="home-content">
        <div className="main-content">
          <motion.h2
            initial={{ opacity: 0, y: -50 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 2 }}
            style={{ fontFamily:"serif",fontSize: "200px", color: "#E8D1C5" }}
          >
            JSMTheBarber
          </motion.h2>
          <Link to="/ScheduleAppt">
            <button>
              <span class="transition"></span>
              <span class="gradient"></span>
              <span class="label">Book Now</span>
            </button>
          </Link>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};

const Layout = ({ children }) => {
  const location = useLocation();

  return (
    <div>
      <AnimatePresence exitBeforeEnter>
        <Switch location={location} key={location.pathname}>
          <Route exact path="/" component={HomePage} />
          {/* Add other routes here */}
        </Switch>
      </AnimatePresence>
    </div>
  );
};
