import React, { useState } from "react";
import "./ScheduleAppt.css";
import { Link } from "react-router-dom"; // Import Link
import Navbar from "../Navbar/Navbar";

export const ScheduleAppt = () => {
  const [fullName, setFullName] = useState("");
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedTime, setSelectedTime] = useState("");
  const [formSubmitted, setFormSubmitted] = useState(false);

  const handleFullNameChange = (event) => {
    setFullName(event.target.value);
  };

  const handleDateChange = (event) => {
    setSelectedDate(event.target.value);
  };

  const handleTimeChange = (event) => {
    setSelectedTime(event.target.value);
  };

  const handleScheduleAppointment = () => {
    setFormSubmitted(true);
  };

  // Calculate min and max dates
  const minDate = new Date().toISOString().split("T")[0]; // Today
  const maxDate = new Date();
  maxDate.setMonth(maxDate.getMonth() + 1); // 1 month from now
  maxDate.setDate(1); // Set to the first day of the next month
  maxDate.setDate(maxDate.getDate() - 1); // Set to the last day of the next month
  const maxDateString = maxDate.toISOString().split("T")[0];

  return (
    <div>
      <Navbar />
      <div className="S-container">
        <div className="appointment-container">
          <form className="appointment-form">
            <p className="sign-up-heading">Schedule Appointment</p>
            <p className="paragraph">Schedule an appointment today</p>
            <div className="input-group">
              <input
                required
                type="text"
                placeholder="Full Name"
                value={fullName}
                onChange={handleFullNameChange}
              />
            </div>
            <div className="input-group">
              <input
                required
                type="date"
                placeholder="Date"
                value={selectedDate}
                onChange={handleDateChange}
                min={minDate} // Set min date to today
                max={maxDateString} // Set max date to the last day of next month
              />
              {formSubmitted && // Show error if form submitted and date is invalid
                (new Date(selectedDate) < new Date() ||
                  new Date(selectedDate) >
                    new Date(
                      new Date().getFullYear(),
                      new Date().getMonth() + 1,
                      0
                    )) && (
                  <p className="error-message">
                    Please select a date between today and the end of the
                    current month.
                  </p>
                )}
            </div>
            <div className="input-group">
              <input
                required
                type="time"
                placeholder="Time"
                value={selectedTime}
                onChange={handleTimeChange}
              />
              {formSubmitted && // Show error if form submitted and time is invalid
                (new Date(`2000-01-01 ${selectedTime}`) <
                  new Date("2000-01-01T10:00:00") ||
                  new Date(`2000-01-01 ${selectedTime}`) >
                    new Date("2000-01-01T18:00:00")) && (
                  <p className="error-message">
                    Please select a time between 10:00 AM and 6:00 PM.
                  </p>
                )}
            </div>
            <button type="button" onClick={handleScheduleAppointment}>
              Schedule
            </button>
          </form>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
