import React, { useState } from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import { HomePage } from "./Homepage/homepage";
import { ScheduleAppt } from "./ScheduleAppt/ScheduleAppt";
import { Login } from "./Login/Login";
import { SignUp } from "./SignUp/SignUp";
import Navbar from "./Navbar/Navbar"; // Import the Navbar component

function RouterFunction() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to manage authentication

  const logout = () => {
    // Perform logout actions, e.g., clear local storage, etc.
    setIsLoggedIn(false);
  };

  return (
    <div>
      <Router>
        {/* Pass isLoggedIn state and logout function to Navbar */}
        <Navbar isLoggedIn={isLoggedIn} logout={logout} />
        <Routes>
          <Route path="/" element={<HomePage />} />
          <Route path="/Login" element={<Login setIsLoggedIn={setIsLoggedIn} />} />
          <Route path="/SignUp" element={<SignUp />} />
          {/* Render ScheduleAppt only if isLoggedIn is true */}
          {isLoggedIn && <Route path="/ScheduleAppt" element={<ScheduleAppt />} />}
        </Routes>
      </Router>
    </div>
  );
}

export default RouterFunction;
