import React from "react";
import { Link } from "react-router-dom";
import "./homepage.css";
import { motion } from "framer-motion";
import Navbar from "../Navbar/Navbar";
import homepagePic from "../Assets/homepagePic.png";
import transition from "../transition";

const HomePage = () => {
  const wordVariants = {
    hidden: {
      opacity: 0,
      x: -50,
    },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 2,
      },
    },
  };

  return (
    <div className="container">
      <Navbar />
      <div className="home-content">
        <div className="main-content">
          <div className="content-wrapper">
            <div style={{ fontFamily:"monospace", fontSize: "200px", color: "#000000", textAlign: "left" }}>
              <motion.div
                initial="hidden"
                animate="visible"
                variants={wordVariants}
                style={{ display: "inline-block" }}
              >
                <div className="image-wrapper">
              <img className ="img" src={homepagePic} alt="Barber" />
            </div>
                <motion.span variants={wordVariants}>JSM</motion.span>
              </motion.div>
              <br />
              <motion.div
                initial="hidden"
                animate="visible"
                variants={wordVariants}
                transition={{ delay: 0.5 }}
                style={{ display: "inline-block" }}
              >
                
                <motion.span variants={wordVariants}>The</motion.span>
              </motion.div>
              <br />
              <motion.div
                initial="hidden"
                animate="visible"
                variants={wordVariants}
                transition={{ delay: 1 }}
                style={{ display: "inline-block" }}
              >
                <motion.span variants={wordVariants}>Barber</motion.span>
              </motion.div>
            </div>
            <Link to="/ScheduleAppt">
              <button>
                <span className="transition"></span>
                <span className="gradient"></span>
                <span className="label">Book Now</span>
              </button>
            </Link>
          </div>
        </div>
      </div>
    </div>
  );
};
