import React, { useState } from "react";
import { Link } from "react-router-dom"; // Import Link
import "./homepage.css";
import { motion, AnimatePresence } from "framer-motion";

export const HomePage = () => {
  const textVariants = {
    hidden: {
      opacity: 0,
      x: -50,
    },
    visible: {
      opacity: 1,
      x: 0,
      transition: {
        duration: 0.5,
      },
    },
  };

  return (
    <div>
      <div className="navbar">
        <a href="/" className="left">
          JSM's Chop Shop
        </a>
        <div className="right">
          {/* Use Link to navigate to "/LoginSignUp" */}
          <Link to="/SignUp">
            <button>Sign Up</button>
          </Link>
          <Link to="/Login">
            <button>Login</button>
          </Link>
        </div>
      </div>
      <div className="home-content">
        <div className="main-content">
          <AnimatePresence>
            <motion.div
              key="JSMTheBarber"
              initial="hidden"
              animate="visible"
              variants={textVariants}
              style={{
                fontFamily: "serif",
                fontSize: "200px",
                color: "#E8D1C5",
                whiteSpace: "pre",
              }}
            >
              {/* Splitting the text into individual letters */}
              {Array.from("JSMTheBarber").map((letter, index) => (
                <motion.span key={index} variants={textVariants}>
                  {letter}
                </motion.span>
              ))}
            </motion.div>
          </AnimatePresence>
          <Link to="/ScheduleAppt">
            <button>
              <span class="transition"></span>
              <span class="gradient"></span>
              <span class="label">Book Now</span>
            </button>
          </Link>
        </div>
      </div>
      <div className="footer">
        <p>&copy; 2024 JSM's Chop Shop. All rights reserved.</p>
      </div>
    </div>
  );
};
