// Login.js
import React, { useState } from "react";
import "./Login.css";
import { Link, useNavigate } from "react-router-dom";
import Axios from "axios";

export const Login = ({ login }) => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [loginStatus, setLoginStatus] = useState("");
  
  const navigate = useNavigate(); // Use useNavigate instead of useHistory

  const handleLogin = (e) => {
    e.preventDefault();
    if (!email || !password) {
      setLoginStatus("Please fill in all fields.");
      return;
    }
    Axios.post("http://localhost:3001/login", {
      email: email,
      password: password,
    })
    .then((response) => {
      if (response.data.token) {
        localStorage.setItem("token", response.data.token);
        login(); // Call the login function passed down from the parent
        navigate("/"); // Navigate to homepage after successful login
      } else {
        setLoginStatus(response.data.message || "Login Failed");
      }
    })
    .catch((error) => {
      console.error("Login error: ", error);
      setLoginStatus("Login failed. Please try again later.");
    });
  };

  return (
    <div className="login-container">
      <form className="login-form">
        <p className="heading">Login</p>
        <p className="paragraph">Login to your account</p>
        <div className="input-group">
          <input
            onChange={(e) => setEmail(e.target.value)}
            required
            placeholder="Email"
            id="email"
            type="email"
          />
        </div>
        <div className="input-group">
          <input
            onChange={(e) => setPassword(e.target.value)}
            required
            placeholder="Password"
            name="password"
            id="password"
            type="password"
          />
        </div>
        <h1 className="login-status">{loginStatus}</h1>
        <button type="submit" onClick={handleLogin}>
          Login
        </button>
        <div className="bottom-text">
          <p>
            Don't have an account? <Link to="/signup">Sign Up</Link>
          </p>
          <p>
            <Link to="#">Forgot password?</Link>
          </p>
        </div>
      </form>
    </div>
  );
};
